mapper.select:
select sm.*, c.name as category_name from setmeal sm
left join category c on sm.category_id = c.id


        and sm.name like concat('%',#{name},'%')


        and sm.category_id = #{categoryId}


        and sm.status = #{status}


order by sm.create_time desc

mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select * from setmeal


        and category_id = #{categoryId}


        and status = #{status}


        and name like concat('%',#{name},'%')

select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
